import { useEffect, useRef } from "react";

const Bounce = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef();

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    if (!ctx) {
      console.error("Canvas context not available");
      return;
    }

    // Set canvas size to match the window
    const setCanvasSize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      console.log("Canvas resized:", canvas.width, canvas.height);
    };

    setCanvasSize();

    const gravity = 0.5;
    const friction = 0.9;

    class Ball {
      constructor(x, y, dx, dy, radius, color) {
        this.x = x;
        this.y = y;
        this.dx = dx;
        this.dy = dy;
        this.radius = radius;
        this.color = color;
        this.bounces = 0;  // Track the number of bounces
        this.opacity = 1;  // Full opacity initially
      }

      draw() {
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2, false);
        ctx.fillStyle = `rgba(${this.color}, ${this.opacity})`; // Make opacity change with "poof"
        ctx.fill();
        ctx.closePath();
      }

      update() {
        if (this.y + this.radius + this.dy > canvas.height) {
          this.dy = -this.dy * friction;
          this.bounces += 1;  // Count bounces
        } else {
          this.dy += gravity;
        }

        if (this.x + this.radius > canvas.width || this.x - this.radius < 0) {
          this.dx = -this.dx;
        }

        // If the ball has bounced 5 times, start the "poof" animation
        if (this.bounces >= 5 && this.opacity > 0) {
          this.radius -= 0.1; // Shrink the ball
          this.opacity -= 0.02; // Fade it out
        }

        this.x += this.dx;
        this.y += this.dy;

        if (this.opacity > 0) {
          this.draw();
        }
      }
    }

    let balls = [];

    const init = () => {
      balls = [];
      for (let i = 0; i < 40; i++) {
        const radius = Math.random() * 15 + 10;
        const x = Math.random() * (canvas.width - radius * 2) + radius;
        const y = Math.random() * (canvas.height - radius * 2);
        const dx = (Math.random() - 0.5) * 4;
        const dy = (Math.random() - 0.5) * 4;
        const color = `${Math.random() * 360}, 70%, 60%`; // HSL color format without alpha
        balls.push(new Ball(x, y, dx, dy, radius, color));
      }
      console.log("Initialized balls:", balls.length);
    };

    const animate = () => {
      animationRef.current = requestAnimationFrame(animate);
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update each ball and remove any that are completely faded
      balls = balls.filter(ball => ball.opacity > 0);
      balls.forEach(ball => ball.update());
    };

    init();
    animate();

    const handleResize = () => {
      setCanvasSize();
      init();
    };

    window.addEventListener("resize", handleResize);

    return () => {
      cancelAnimationFrame(animationRef.current);
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        zIndex: 0,
        width: "100vw",
        height: "100vh",
        display: "block",
        background: "transparent",
        //border: "2px solid red", // For debugging visibility
      }}
    />
  );
};

export default Bounce;
