import { Heading, HStack, Image, Text, VStack, Box, Link } from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Card = ({ title, description, imageSrc, url}) => {
    //console.log("Card received imageSrc:", imageSrc);
  return (
    <VStack
      key={title}
      color="black"
      //border="1px solid #ccc"
      backgroundColor="white"
      boxShadow="0 2px 8px rgba(0, 0, 0, 0.1)"
      _hover={{
        boxShadow: "0 8px 20px rgba(0, 0, 0, 0.5)",  // Hover effect for depth
        borderColor: "#C3922A",  // Slightly darker gold border on hover
      }}
      overflow="hidden"
      justifyContent="space-between"
      //border="5px solid #D4AF37"  // Gold border to make it premium
      border="10px solid transparent"  // Start with transparent border
      borderRadius="12px"  // Rounded corners for elegance
      backgroundClip="border-box"  // Ensures the gradient is applied to the border
      borderImage="linear-gradient(to right, #FF7E5F, #FEB47B) 1"  // Cool gradient border
      >
      <Image
      src={imageSrc}
      alt="Card Image"
      width="100%"
      height="auto" />

      <Box p="10px">
        <Heading as="h3" size="md" marginBottom="10px" alignSelf="start" >
          {title}
        </Heading>
        <Text marginBottom="10px">
          {description}
        </Text>
        <Link href={url} color="teal.500" isExternal>
          See more <FontAwesomeIcon icon={faArrowRight} />
        </Link>
      </Box>
  </VStack>);
};

export default Card;

