import { useEffect, useRef } from "react";

const Bounce = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef();

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    if (!ctx) {
      console.error("Canvas context not available");
      return;
    }

    // Set canvas size to match the window
    const setCanvasSize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      console.log("Canvas resized:", canvas.width, canvas.height);
    };

    setCanvasSize();

    const gravity = 0.7;
    const friction = 0.9;
    const maxSpeed = 10; // Max speed of the balls to prevent them from going too fast

    class Ball {
      constructor(x, y, dx, dy, radius, color) {
        this.x = x;
        this.y = y;
        this.dx = dx;
        this.dy = dy;
        this.radius = radius;
        this.color = color;
        this.bounces = 0; // Track number of bounces
        this.opacity = 1; // Opacity for the "poof" effect
      }

      draw() {
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2, false);
        ctx.fillStyle = this.color;
        ctx.globalAlpha = this.opacity; // Use opacity for fading effect
        ctx.fill();
        ctx.closePath();
      }

      update() {
        // Check for bounce from the ground
        if (this.y + this.radius + this.dy > canvas.height) {
          this.dy = -this.dy * friction; // Reverse and apply friction on bounce
          this.bounces += 1; // Increment bounce count
        } else {
          // Apply constant gravity to the vertical speed after the ball has bounced
          if (this.dy < maxSpeed) {
            this.dy += gravity; // Gravity only increases the speed until max speed
          }
        }

        // Check for wall collisions (bounce horizontally)
        if (this.x + this.radius > canvas.width || this.x - this.radius < 0) {
          this.dx = -this.dx; // Reverse horizontal speed
        }

        // Limit the speed to prevent uncontrollable motion
        if (Math.abs(this.dx) > maxSpeed) {
          this.dx = maxSpeed * Math.sign(this.dx); // Limit horizontal speed
        }

        if (Math.abs(this.dy) > maxSpeed) {
          this.dy = maxSpeed * Math.sign(this.dy); // Limit vertical speed
        }

        // Update ball position
        this.x += this.dx;
        this.y += this.dy;

        // If the ball has bounced 5 times, start the "poof" animation
        if (this.bounces >= 5 && this.opacity > 0) {
          this.radius -= 0.1; // Shrink the ball to simulate it vanishing
          this.opacity -= 0.02; // Fade it out gradually
        }

        // Draw the ball if it's not fully transparent
        if (this.opacity > 0) {
          this.draw();
        }
      }
    }

    let balls = [];

    const init = () => {
      balls = [];
      for (let i = 0; i < 40; i++) {
        const radius = Math.random() * 15 + 10;
        const x = Math.random() * (canvas.width - radius * 2) + radius;
        const y = Math.random() * (canvas.height - radius * 2);
        const dx = (Math.random() - 0.5) * 4;
        const dy = (Math.random() - 0.5) * 4;
        const color = `hsl(${Math.random() * 360}, 70%, 60%)`;
        balls.push(new Ball(x, y, dx, dy, radius, color));
      }
      console.log("Initialized balls:", balls.length);
    };

    const animate = () => {
      animationRef.current = requestAnimationFrame(animate);
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      balls.forEach(ball => ball.update());
    };

    const handleResize = () => {
      setCanvasSize();
      init();
    };

    const handleScroll = () => {
      if (window.scrollY === 0) {
        console.log("Scrolled to the top, resetting the animation.");
        init();  // Reinitialize the balls
        animate(); // Restart animation
      }
    };

    window.addEventListener("resize", handleResize);
    window.addEventListener("scroll", handleScroll);

    // Initialize and start animation
    init();
    animate();

    return () => {
      cancelAnimationFrame(animationRef.current);
      window.removeEventListener("resize", handleResize);
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        zIndex: 0,
        width: "100vw",
        height: "100vh",
        display: "block",
        background: "transparent",
        border: "2px solid red", // For debugging visibility
      }}
    />
  );
};

export default Bounce;
