import { Heading, HStack, Image, Text, VStack, Box, Link } from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Card = ({ title, description, imageSrc, url}) => {
    //console.log("Card received imageSrc:", imageSrc);
  return (
    <VStack
      key={title}
      color="black"
      border="1px solid #ccc"
      borderRadius="8px"
      backgroundColor="white"
      boxShadow="0 2px 8px rgba(0, 0, 0, 0.1)"
      _hover={{ boxShadow: "0 4px 12px rgba(0, 0, 0, 0.15)" }}
      overflow="hidden"
      >
      <Image
      src={imageSrc}
      alt="Card Image"
      width="100%"
      height="auto" />

      <Box p="10px">
        <Heading as="h3" size="md" marginBottom="10px" alignSelf="start" >
          {title}
        </Heading>
        <Text marginBottom="10px">
          {description}
        </Text>
        <Link href={url} color="teal.500" isExternal>
          See more <FontAwesomeIcon icon={faArrowRight} />
        </Link>
      </Box>
  </VStack>);
};

export default Card;

