import React from "react";
import { Avatar, Heading, VStack, Text, Box } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";
import myselfImage from "../images/myself.jpg";
import Bounce from "./Bounce";
import Header from "./Header";

const greeting = "Hello there, I'm Shizhen Zhao!";
const bio1 = "NUS Computer Science undergrad";
const bio2 = "Python, SQL, C, R, Flask, and React, with a focus on web development and data analysis";

// Implement the UI for the LandingSection component according to the instructions.
// Use a combination of Avatar, Heading and VStack components.
const LandingSection = () => (
  <FullScreenSection
  style={{
    display: "flex",
    flexDirection: "column",     // Stack children vertically
    height: "100vh",             // Total height of the section
    width: "100%"}}
  >
    <Box width="100%">
    <Header />
    </Box>
    <Box

    style={{ position: "relative", overflow: "hidden", flexgrow}}
    justifyContent="center"
    alignItems="center"
    width="100%"       // Ensure full width of the container
    //style={{border: "1px solid red"}}
    isDarkBackground
    backgroundColor="#121212">
    <Bounce/>
    <VStack

    zIndex={1}
    spacing={0}
    align="center"
    //style={{border: "1px solid red"}}
    >
      <Avatar
        boxSize="250px"
        src={myselfImage}
        alt="Avatar"
      />
      <Text>{greeting}</Text>
        <Heading mt="20px" align="center">
          <Text fontSize="lg" mb={2} color="#DAA520">
            {bio1}
          </Text>
          <Text fontSize="md" color="#D3D3D3">
            {bio2}
          </Text>
          </Heading>
      </VStack>
      </Box>
  </FullScreenSection>
);

export default LandingSection;
