import React from "react";
import { VStack, Heading, Text, Box, Avatar, SimpleGrid } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";
import hackathonIcon from "../images/hackathon.png";

// ðŸ§  Step 1: Define your list of hackathons here
const hackathons = [
  {
    title: "DSTA BrainHack 2024",
    description:
      "Built a multi-agent reinforcement learning system in a 16x16 grid world using PPO and GRUs. Trained role-conditioned agents in a sparse reward setting.",
  },
  {
    title: "Shopee Code League",
    description:
      "Solved challenging real-world ML problems like item classification and search ranking using Python and pandas. Ranked top 5% in leaderboard.",
  },
  {
    title: "NUS Hack&Roll 2025",
    description:
      "Created a real-time ASR app that transcribes muffled and noisy speech using Whisper fine-tuned with custom audio samples and preprocessing.",
  },
];

const HackathonSection = () => {
  return (
    <FullScreenSection
      justifyContent="center"
      alignItems="center"
      isDarkBackground
      backgroundColor="#121212"
      w="100vw"
    >
      <Box height={["15vh", "0vh", "0vh"]}></Box>

      <VStack zIndex={1} spacing={[4, 6, 8]} align="center">
        <Avatar
          boxSize={["120px", "160px", "200px"]}
          src={hackathonIcon}
          alt="Hackathon Icon"
        />

        <Heading
          mt={["10px", "15px", "20px"]}
          fontSize={["lg", "xl", "2xl"]}
          textAlign="center"
          color="#DAA520"
        >
          Hackathons & Competitions
        </Heading>

        <Text
          fontSize={["sm", "md", "lg"]}
          color="#D3D3D3"
          maxW="80%"
          textAlign="center"
        >
          I actively participate in hackathons to sharpen my skills and build impactful solutions. These challenges fuel my passion for AI and real-world innovation.
        </Text>

        {/* Step 2: Map over the hackathons array */}
        <SimpleGrid columns={[1, 1, 2]} spacing={8} maxW="80%" pt={6}>
          {hackathons.map((hackathon, index) => (
            <Box
              key={index}
              p={5}
              borderRadius="md"
              background="#1E1E1E"
              boxShadow="md"
              color="white"
            >
              <Heading size="md" mb={2} color="#DAA520">
                {hackathon.title}
              </Heading>
              <Text fontSize="sm" color="#CCCCCC">
                {hackathon.description}
              </Text>
            </Box>
          ))}
        </SimpleGrid>
      </VStack>
    </FullScreenSection>
  );
};

export default HackathonSection;
