import React, { useEffect, useState } from "react";
import { Avatar, Heading, VStack, Text } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";
import myselfImage from "../images/myself.jpg";

const greeting = "Hello there, I'm Shizhen Zhao!";
const bio1 = "NUS Computer Science undergrad";
const bio2 = "Python, SQL, C, R, Flask, and React, with a focus on web development and data analysis";

const LandingSection = () => {
  const [rotation, setRotation] = useState(0);
  const [opacity, setOpacity] = useState(1); // State to track opacity

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const rotationValue = scrollPosition / 0.6; // Adjust the divisor for rotation speed
      setRotation(rotationValue); // Update rotation value based on scroll position

      // Calculate opacity based on scroll position
      // Fade out as we scroll down
      const opacityValue = 1 - scrollPosition / window.innerHeight; // Opacity decreases as you scroll down
      if (opacityValue >= 0) {
        setOpacity(opacityValue);
      }
    };

    window.addEventListener("scroll", handleScroll);

    // Cleanup on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <FullScreenSection
      justifyContent="center"
      alignItems="center"
      isDarkBackground
      backgroundColor="#121212"
      style={{
        transform: `rotate(${rotation}deg)`,
        opacity: opacity, // Apply calculated opacity
        transition: "transform 0.1s ease-out, opacity 0.3s ease-out", // Smooth transition for rotation and opacity
      }}
    >
      <VStack zIndex={1} spacing={0} align="center">
        <Avatar boxSize="250px" src={myselfImage} alt="Avatar" />
        <Text>{greeting}</Text>
        <Heading mt="20px" align="center">
          <Text fontSize="lg" mb={2} color="#DAA520">
            {bio1}
          </Text>
          <Text fontSize="md" color="#D3D3D3">
            {bio2}
          </Text>
        </Heading>
      </VStack>
    </FullScreenSection>
  );
};

export default LandingSection;
